import "./App.css";

import axios from "axios";
import { useState } from "react";
import { useEffect } from "react";

function App() {
  const baseUrl = "http://localhost:50944/api/dcandidate";

  const [data, setData] = useState([]);

  const getRequest = async () => {
    await axios
      .get(baseUrl)
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    getRequest();
  });

  return (
    <div className="App">
      <br />
      <h3>Cadastro de Doação</h3>
      <header>
        <button className="btn btn-success">Adicionar novo cadastro</button>
      </header>
      <form className="formulario" method="post" action="#">
        <div className="lefthalf">
          <input type="text" placeholder="Nome" required=""/>
          <input type="email" placeholder="E-mail" required=""/>
          <input type="text" placeholder="Assunto" required=""/>
        </div>

        <div className="metade right">
          <textarea name="mensagem" type="text" placeholder="Mesagem" required=""></textarea>
        </div>

        <input type="submit" value="Enviar Dados" class="btn-submit" />

      </form>



      <table className="table table-bordered">
        <thead>
          <tr>
            <th>Id</th>
            <th>Nome</th>
            <th>Telefone</th>
            <th>Tipo sanguíneo</th>
            <th></th>
          </tr>
        </thead>
        <tbody> 
          {data.map(dcandidate => (
            <tr key={dcandidate.id}>
              <td>{dcandidate.id}</td>
              <td>{dcandidate.fullName}</td>
              <td>{dcandidate.mobile}</td>
              <td>{dcandidate.bloodGroup}</td>
              <td>
                <button className="btn btn-primary">Editar</button>
                <button className="btn btn-danger">Excluir</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
