import "./App.css";

import axios from "axios";
import { useState, useEffect } from "react";
import react, { Component } from 'react';

function App() {
  const baseUrl = "http://localhost:50944/api/dcandidate";

  const [data, setData] = useState([]);
  const [selectCandidate, setSelectCandidate] = useState({
    id: "",
    fullName: "",
    email: "",
    bloodGroup: "",
    mobile: "",
    age: "",
    address: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setSelectCandidate({ ...selectCandidate, [name]: value });
    console.log(selectCandidate);
  };

  const getRequest = async () => {
    await axios
      .get(baseUrl)
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const postRequest = async () => {
    delete selectCandidate.id;
    selectCandidate.age = parseInt(selectCandidate.age);
    await axios
      .post(baseUrl, selectCandidate)
      .then((response) => {
        setData(data.concat(response.data));
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    getRequest();
  });

  useEffect(() => {
    postRequest();
  });

  return (
    <div className="App">
      <br />
      <h1>Cadastro de Doação</h1>

      <form className="form-control" method="post" action="#">
        <div className="half left">
          <input
            type="text"
            placeholder="Nome"
            name="fullName"
            onChange={handleChange}
          />
          <input
            type="email"
            placeholder="E-mail"
            name="email"
            onChange={handleChange}
          />
          <select
            className="form-select"
            name="bloodGroup"
            onChange={handleChange}
          >
            <option seleted>Blood Group</option>
            <option value="O+">O+</option>
            <option value="O-">O-</option>
            <option value="A+">A+</option>
            <option value="A-">A-</option>
            <option value="B+">B+</option>
            <option value="B-">B-</option>
            <option value="AB+">AB+</option>
            <option value="AB-">AB-</option>
          </select>
        </div>

        <div className="half right">
          <input
            type="text"
            placeholder="Telefone"
            name="mobile"
            onChange={handleChange}
          />
          <input
            type="number"
            placeholder="Idade"
            name="age"
            onChange={handleChange}
          />
          <input
            type="text"
            placeholder="Cidade"
            name="address"
            onChange={handleChange}
          />
        </div>

        <input
          type="submit"
          value="Enviar Dados"
          class="btn-submit"
          onClick={() => postRequest()}
        />
        <button type="submit" onClick={postRequest}></button>
      </form>

      <table className="table table-bordered">
        <thead>
          <tr>
            <th>Id</th>
            <th>Nome</th>
            <th>Telefone</th>
            <th>Tipo sanguíneo</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {data.map((dcandidate) => (
            <tr key={dcandidate.id}>
              <td>{dcandidate.id}</td>
              <td>{dcandidate.fullName}</td>
              <td>{dcandidate.mobile}</td>
              <td>{dcandidate.bloodGroup}</td>
              <td>
                <button className="btn btn-primary">Editar</button>
                <button className="btn btn-danger">Excluir</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
