import "./App.css";

import axios from "axios";
import { useState } from "react";
import { useEffect } from "react";

function App() {
  const baseUrl = "http://localhost:50944/api/dcandidate";

  const [data, setData] = useState([]);
  const [selectCandidate, setSelectCandidate] = useState({
    id: "",
    fullName: "",
    email: "",
    mobile: "",
    age: "",
    address: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setSelectCandidate({ ...selectCandidate, [name]: value });
    console.log(se)
  };

  const getRequest = async () => {
    await axios
      .get(baseUrl)
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    getRequest();
  });

  return (
    <div className="App">
      <br />
      <h3>Cadastro de Doação</h3>
      <header>
        <button className="btn btn-success">Adicionar novo cadastro</button>
      </header>

      <form className="form-control" method="post" action="#">
        <div className="half left">
          <input type="text" placeholder="Nome" required="" />
          <input type="email" placeholder="E-mail" required="" />
          <select name="select-response" className="form-select">
            <option seleted>Blood Group</option>
            <option value="1">O+</option>
            <option value="2">O-</option>
            <option value="3">A+</option>
            <option value="4">A-</option>
            <option value="5">B+</option>
            <option value="6">B-</option>
            <option value="7">AB+</option>
            <option value="8">AB-</option>
          </select>
        </div>

        <div className="half right">
          <input type="text" placeholder="Telefone" required="" />
          <input type="number" placeholder="Idade" required="" />
          <input type="text" placeholder="Cidade" required="" />
        </div>

        <input type="submit" value="Enviar Dados" class="btn-submit" />
      </form>

      <table className="table table-bordered">
        <thead>
          <tr>
            <th>Id</th>
            <th>Nome</th>
            <th>Telefone</th>
            <th>Tipo sanguíneo</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {data.map((dcandidate) => (
            <tr key={dcandidate.id}>
              <td>{dcandidate.id}</td>
              <td>{dcandidate.fullName}</td>
              <td>{dcandidate.mobile}</td>
              <td>{dcandidate.bloodGroup}</td>
              <td>
                <button className="btn btn-primary">Editar</button>
                <button className="btn btn-danger">Excluir</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
