import "./App.css";

import axios from "axios";
import { useState, useEffect } from "react";
import Modal from "react-modal";
import { ModalFooter } from "reactstrap";

function App() {
  const baseUrl = "http://localhost:50944/api/dcandidate";

  const [data, setData] = useState([]);
  const [selectCandidate, setSelectCandidate] = useState({
    id: "",
    fullName: "",
    email: "",
    bloodGroup: "",
    mobile: "",
    age: "",
    address: "",
  });
  const [editModal, setEditModal] = useState(false);
  const [modalIsOpen, setIsOpen] = useState(false);

  const openModal = () => {
    setIsOpen(true);
  };

  const closeModal = () => {
    setIsOpen(false);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setSelectCandidate({ ...selectCandidate, [name]: value });
    console.log(selectCandidate);
  };

  const getRequest = async () => {
    await axios
      .get(baseUrl)
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const postRequest = async () => {
    delete selectCandidate.id;
    selectCandidate.age = parseInt(selectCandidate.age);
    await axios
      .post(baseUrl, selectCandidate)
      .then((response) => {
        setData(data.concat(response.data));
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const putRequest = async () => {

  }

  const deleteRequest = async () => {

  }

  const selectDCandidate = (dcandidate, opcao) => {
    setSelectCandidate(dcandidate);
    (opcao === "Edit") && openAndCloseEditModal();
  };

  const openAndCloseEditModal = () => {
    setEditModal(!editModal);
  };

  useEffect(() => {
    getRequest();
  });

  return (
    <div className="App">
      <br />
      <h1>Cadastro de Doação</h1>

      <form className="form-control">
        <div className="half left">
          <input
            type="text"
            placeholder="Nome"
            name="fullName"
            onChange={handleChange}
          />
          <input
            type="email"
            placeholder="E-mail"
            name="email"
            onChange={handleChange}
          />
          <select
            className="form-select"
            name="bloodGroup"
            onChange={handleChange}
          >
            <option seleted placeholder="Blood Group"></option>
            <option value="O+">O+</option>
            <option value="O-">O-</option>
            <option value="A+">A+</option>
            <option value="A-">A-</option>
            <option value="B+">B+</option>
            <option value="B-">B-</option>
            <option value="AB+">AB+</option>
            <option value="AB-">AB-</option>
          </select>
        </div>

        <div className="half right">
          <input
            type="text"
            placeholder="Telefone"
            name="mobile"
            onChange={handleChange}
          />
          <input
            type="number"
            placeholder="Idade"
            name="age"
            onChange={handleChange}
          />
          <input
            type="text"
            placeholder="Cidade"
            name="address"
            onChange={handleChange}
          />
        </div>
        <input
          type="submit"
          value="Enviar Dados"
          class="btn-submit"
          onClick={() => postRequest()}
        />
      </form>

      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Example Modal"
        overlayClassName="modal-overlay"
        className="modal-content"
      >
        <h2>Editar Candidato</h2>
        <hr />
        <form className="form-control">
        <div className="half left">
          <input
            type="text"
            placeholder="Nome"
            name="fullName"
            onChange={handleChange}
          />
          <input
            type="email"
            placeholder="E-mail"
            name="email"
            onChange={handleChange}
          />
          <select
            className="form-select"
            name="bloodGroup"
            onChange={handleChange}
          >
            <option seleted>Blood Group</option>
            <option value="O+">O+</option>
            <option value="O-">O-</option>
            <option value="A+">A+</option>
            <option value="A-">A-</option>
            <option value="B+">B+</option>
            <option value="B-">B-</option>
            <option value="AB+">AB+</option>
            <option value="AB-">AB-</option>
          </select>
        </div>

        <div className="half right">
          <input
            type="text"
            placeholder="Telefone"
            name="mobile"
            onChange={handleChange}
          />
          <input
            type="number"
            placeholder="Idade"
            name="age"
            onChange={handleChange}
          />
          <input
            type="text"
            placeholder="Cidade"
            name="address"
            onChange={handleChange}
          />
        </div>
      </form>

      <ModalFooter className="modal-fotter">
        <button>Salvar</button>
        <button onClick={closeModal}>Close</button>
      </ModalFooter>
      </Modal>

      <table className="table table-bordered">
        <thead>
          <tr>
            <th>Id</th>
            <th>Nome</th>
            <th>Telefone</th>
            <th>Tipo sanguíneo</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {data.map((dcandidate) => (
            <tr key={dcandidate.id}>
              <td>{dcandidate.id}</td>
              <td>{dcandidate.fullName}</td>
              <td>{dcandidate.mobile}</td>
              <td>{dcandidate.bloodGroup}</td>
              <td>
                <button
                  className="btn btn-primary"
                  onClick={openModal}
                >
                  Editar
                </button>
                <button
                  className="btn btn-danger"
                  onClick={() => selectDCandidate(dcandidate, "Delete")}
                >
                  Excluir
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
